import React, { useEffect } from "react";
import {
  Card,
  Typography,
  Table,
  Tag,
  Image,
  Button,
  Space,
  message,
} from "antd";
import { CloudDownloadOutlined, FilePdfOutlined } from "@ant-design/icons";

const { Title, Text } = Typography;

const ProgramPreview = ({ program, onDownload, onPdfPreview }) => {
  if (!program) return null;

  // Debug image URL when component renders
  useEffect(() => {
    if (program) {
      console.log("Program data:", {
        title: program.title,
        imageUrl: program.image_url,
        hasImage: !!program.image_url,
      });
    }
  }, [program]);

  // Prepare exercises for the table
  const exercises = program.exercises?.filter(Boolean) || [];

  const columns = [
    // ...your existing columns
  ];

  return (
    <Card className="program-preview">
      <div className="program-header mb-6">
        <div className="flex flex-col md:flex-row md:items-start">
          {/* Image display with Ant Design Image component */}
          {program.image_url ? (
            <div className="mb-4 md:mb-0 md:mr-6" style={{ minWidth: "280px" }}>
              <Image
                src={program.image_url}
                alt={program.title}
                style={{
                  maxWidth: "100%",
                  borderRadius: "8px",
                  objectFit: "cover",
                }}
                fallback="data:image/png;base64,"
                preview={{
                  mask: "View image",
                }}
                onError={() => {
                  console.error("Failed to load image:", program.image_url);
                  message.error("Failed to load program image");
                }}
              />
            </div>
          ) : (
            <div className="mb-4 md:mb-0 md:mr-6" style={{ minWidth: "280px" }}>
              <div
                style={{
                  width: "280px",
                  height: "160px",
                  background: "#f0f0f0",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  borderRadius: "8px",
                  color: "#999",
                }}
              >
                No Image Available
              </div>
            </div>
          )}

          <div className="flex-1">
            <Title level={3}>{program.title}</Title>
            <Text type="secondary">{program.description}</Text>

            <div className="mt-4 flex flex-wrap gap-2">
              <Tag color="blue">{program.goal_type?.replace("_", " ")}</Tag>
              <Tag color="green">
                Difficulty: {program.difficulty || program.difficulty_level}
              </Tag>
              <Tag color="orange">
                Duration: {program.duration_weeks || program.duration} weeks
              </Tag>
              {program.is_featured && <Tag color="gold">Featured</Tag>}
            </div>
          </div>
        </div>
      </div>

      <div className="program-actions mb-4">
        <Space>
          <Button
            type="primary"
            icon={<CloudDownloadOutlined />}
            onClick={() => onDownload && onDownload(program.program_id)}
          >
            Download Program
          </Button>

          <Button
            icon={<FilePdfOutlined />}
            onClick={() => onPdfPreview && onPdfPreview(program.program_id)}
          >
            Preview PDF
          </Button>
        </Space>
      </div>

      <div className="program-exercises">
        <Title level={4}>Exercises</Title>

        {exercises.length > 0 ? (
          <Table
            columns={columns}
            dataSource={exercises.map((ex, idx) => ({ ...ex, key: idx }))}
            pagination={false}
            scroll={{ x: 1000 }}
            size="middle"
          />
        ) : (
          <Text type="secondary">No exercises found for this program.</Text>
        )}
      </div>
    </Card>
  );
};

export default ProgramPreview;
